mapper.select:
select 

from product_interest_info
where 1=1 
  
	and id = #{id,jdbcType=INTEGER}
  
  
	and is_del = #{isDel,jdbcType=INTEGER}
  
  
	and is_bidden = #{isBidden,jdbcType=INTEGER}
  
  
    and p_income_payment_unit = #{pIncomePaymentUnit,jdbcType=INTEGER}
  
  
    and p_interest_way = #{pInterestWay,jdbcType=INTEGER}
  
  
    and p_interest_rate_day = #{pInterestRateDay,jdbcType=INTEGER}
  
  
    and p_interest_rate_year = #{pInterestRateYear,jdbcType=INTEGER}
  
  
    and p_interest_rate_month = #{pInterestRateMonth,jdbcType=INTEGER}
  
  
    and p_interest_model = #{pInterestModel,jdbcType=INTEGER}
  
  
    and p_expiry_day = #{pExpiryDay,jdbcType=INTEGER}
  
  
    and p_expiry_month = #{pExpiryMonth,jdbcType=INTEGER}
  
  
    and p_expiry_year = #{pExpiryYear,jdbcType=TINYINT}
  
  
    and create_date = #{createDate,jdbcType=TIMESTAMP}
  
  
    and create_user = #{createUser,jdbcType=BIGINT}
  
  
    and update_date = #{updateDate,jdbcType=TIMESTAMP}
  
  
    and update_user = #{updateUser,jdbcType=BIGINT}
