mapper.select:
select 

from sys_file
where 1=1 
  
	and id = #{id,jdbcType=INTEGER}
  
  
	and is_del = #{isDel,jdbcType=INTEGER}
  
  
	and is_bidden = #{isBidden,jdbcType=INTEGER}
  
  
    and file_name = #{fileName,jdbcType=VARCHAR}
  
  
    and file_path = #{filePath,jdbcType=VARCHAR}
  
  
    and file_size = #{fileSize,jdbcType=INTEGER}
  
  
    and file_type = #{fileType,jdbcType=INTEGER}
  
  
    and file_category = #{fileCategory,jdbcType=INTEGER}
  
  
    and file_title = #{fileTitle,jdbcType=VARCHAR}
  
  
    and file_link = #{fileLink,jdbcType=VARCHAR}
  
  
    and remark = #{remark,jdbcType=VARCHAR}
  
  
    and create_date = #{createDate,jdbcType=TIMESTAMP}
  
  
    and create_user = #{createUser,jdbcType=BIGINT}
  
  
    and update_date = #{updateDate,jdbcType=TIMESTAMP}
  
  
    and update_user = #{updateUser,jdbcType=BIGINT}
