mapper.select:
select 

from sys_menu
where 1=1 
  
	and id = #{id,jdbcType=INTEGER}
  
  
	and is_del = #{isDel,jdbcType=INTEGER}
  
  
	and is_bidden = #{isBidden,jdbcType=INTEGER}
  
  
    and menu_name = #{menuName,jdbcType=VARCHAR}
  
  
    and menu_link = #{menuLink,jdbcType=VARCHAR}
  
  
    and menu_pid = #{menuPid,jdbcType=BIGINT}
  
  
    and menu_category = #{menuCategory,jdbcType=INTEGER}
  
  
    and image_file_id = #{imageFileId,jdbcType=BIGINT}
  
  
    and reamrk = #{reamrk,jdbcType=VARCHAR}
  
  
    and create_date = #{createDate,jdbcType=TIMESTAMP}
  
  
    and create_user = #{createUser,jdbcType=BIGINT}
  
  
    and update_date = #{updateDate,jdbcType=TIMESTAMP}
  
  
    and update_user = #{updateUser,jdbcType=BIGINT}
  
  
    and version = #{version,jdbcType=INTEGER}
